import React from 'react';
import logo from './logo.svg';
import './App.css';
import firebaseApp from "./firebase.js"

var field = '';
var value = '';
var bubbles = [];

function getWords() {
  var db = firebaseApp.firestore();
  db.collection("words").where(field, '==', value).get()
    .then((snapshot) => {
      snapshot.forEach((doc) => {
       return bubbles = snapshot.val();
      });
    })
}

